{
	GP_EditBrush_Data *data = (GP_EditBrush_Data *)(ptr->data);
	if (value) data->flag |= 4;
	else data->flag &= ~4;
}

int GPencilSculptBrush_affect_pressure_get(PointerRNA *ptr)
{
	GP_EditBrush_Data *data = (GP_EditBrush_Data *)(ptr->data);
	return (((data->flag) & 8) != 0);
}

void GPencilSculptBrush_affect_pressure_set(PointerRNA *ptr, int value)
{
	GP_EditBrush_Data *data = (GP_EditBrush_Data *)(ptr->data);
	if (value) data->flag |= 8;
	else data->flag &= ~8;
}

int GPencilSculptBrush_direction_get(PointerRNA *ptr)
{
	GP_EditBrush_Data *data = (GP_EditBrush_Data *)(ptr->data);
	return ((data->flag) & 1);
}

void GPencilSculptBrush_direction_set(PointerRNA *ptr, int value)
{
	GP_EditBrush_Data *data = (GP_EditBrush_Data *)(ptr->data);
	data->flag &= ~1;
	data->flag |= value;
}






